foreach (XmlNode node in xmlNodeList)
{
  foreach (XmlNode elementNode in node.XmlNodes)
  {
    if(elementNode.Name == "content")
    {
      if(elementNode.Attributes["lang"] == "ja-JP")
      {
        asset["page_body"] = Util.EscapeItem(elementNode.Value);
      }
    }
  }
}

